import React, { Component } from 'react';
import './ListRow.css'

class ListRow extends Component {
    constructor(props){
        super(props);
        this.state = {
            des : props.des,
            key : props.key
        }
    }

    SendDeleteMessage = (key) => {
        alert(key);
    }

    render() {
        const ListRowField = (
            <div className="work-row">
                &nbsp;&nbsp;
                <input type="checkbox"></input>
                &nbsp; {this.state.des}
                <button className="btn btn-danger" onClick={this.SendDeleteMessage(this.state.key)}>Delete</button>
                <hr></hr>
            </div>
        );

        return ListRowField;
    }
}

export default ListRow;
