import React, { Component } from "react";
import "./App.css";
import ListRow from "./ListWork/ListRow";

class App extends Component {
  countWork = 0;

  constructor(props) {
    super(props);
    this.state = {
      description: "",
      list: [],
    };
    this.GetData();
  }

  GetData(){
    fetch('http://localhost:8081/get-works')
    .then(result => result.json())
    .then((res) => {
      this.setState({
        list: res.data
      })
    })
  }

  DeleteWork = (id) => {
    alert(id);
  }

  DescriptionChangeEvent = (event) => {
    this.setState({ description: event.target.value.toString() });
  };

  AddWorkEvent = (event) => {
    event.preventDefault();
    if (this.state.description !== "") {
      var row = {
        isdone: false,
        description: this.state.description
      }
      debugger
      fetch('http://localhost:8081/add-work', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(row)
      }).then(function(response) {
        console.log(response)
      });

      document.getElementById("add-work-form").reset();
    } else {
      alert("none description input");
    }
  };

  render() {
    return (
      <div className="cont">
        {/* Form add new work */}
        <div className="add-work-field">
          <div className="form-group">
            <form id="add-work-form">
              <input
                placeholder="What needs to be done?"
                className="form-control"
                onChange={this.DescriptionChangeEvent}
              >
              </input>
              <button
                type="submit"
                className="btn btn-primary"
                onClick={this.AddWorkEvent}
              >
                Add
              </button>
            </form>
          </div>
        </div>
        <div className="list-work-field">
          <span>TODO LIST</span>
          <hr></hr>
          <div className="list-work">{
            this.state.list.map((value, index) => {
              return (
                <ListRow
                  isdone={value.isdone}
                  des={value.description}
                  id={value._id}
                  key={value._id}
                  deleteWork={this.DeleteWork}
                ></ListRow>
              )
            })
          }</div>
        </div>
      </div>
    );
  }
}

export default App;
