import React, { Component } from 'react';
import './AddWork.css'

var description = "";

var DescriptionChangeEvent = (event) => {
    description = event.target.value.toString();
}

var AddWorkEvent = (event) => {
    event.preventDefault();
    alert(description);
}



class AddWork extends Component {
    render() {
        const AddWorkField = (
            <div className="add-work-field">
                <div className="form-group">
                    <form>
                        <input placeholder="What needs to be done?" className="form-control" onChange={DescriptionChangeEvent}></input>
                        <button type="submit" className="btn btn-primary" onClick={AddWorkEvent}>Add</button>
                    </form>
                </div>
            </div>
        );

        return AddWorkField;
    }
}

export default AddWork;
